// Genere // Data di nascita

// Email

// Password
// Conferma Password

// Button Conferma

export default class Registration extends Component {
    constructor(props){
        super(props)
        this.state ={
            customer: {
                name :  '',

            }
        }
    }

    handleName = (e) => {
        this.name = e
    }

    handleSurname = (e) => {
        this.surname = e
    }

    handleEmail = (e) => {
        this.email = e
    }

    handlePassword = (e) => {
        this.password = e
    }
    handleSubmit() {
        if (utilities.checkEmailValidity(this.email) && 
        utilities.checkPasswordValidity(this.name) && 
        utilities.checkPersonal(this.name) && 
            utilities.checkPersonal(this.surname)) {
            console.log("zoppu fuaitti")
        }

        this.setState({
            customer: {
                name: this.name,
                surname: this.surname,
                email: this.email,
                password: this.password,

            }
        })
    }

    render() {
        return (
            <>
                <Title title={"Registration"} subtitle={"Don't have an account?"} />
                <div className="mainForm">
                    <div className="Container-form">

                        <div className="personal">
                            <ClassInputBox
                                placeholder={'Name'}
                                name={'Name'}
                                type={'text'}
                                callbackInput={this.handleName}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Surname'}
                                name={'Surname'}
                                type={'text'}
                                callbackInput={this.handleSurname}
                                maxlength={24}
                                cssName={'text'}
                            />
                        </div>

                        <div className="data-gender">
                            {/* <ClassInputBox
                                placeholder={'Name'}
                                name={'Name'}
                                type={'text'}
                                callbackInput={this.handleName}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Surname'}
                                name={'Surname'}
                                type={'text'}
                                callbackInput={this.handlePersonal}
                                maxlength={24}
                                cssName={'text'}
                            /> */}
                        </div>

                        <ClassButton
                            label={'SIGN UP'}
                            cssName={'Sign-in'}
                            callbackButton={this.handleSubmit}
                        />
                    </div>

                    <div className='Check' >
                        <span>
                            <input type='checkbox' id='RememberMe' />
                            <label htmlFor='RememberMe'>Remember Me</label>
                        </span>

                        <ClassButton
                            label={'Forgot Password'}
                            cssName={'forgot-password'}
                        />
                        {/* </div> */}
                    </div>

                    {/* <div className='signWith'>
                        <Link to="/registration" style={{ color: 'white', textDecoration: 'none' }}>Registration</Link>
                    </div> */}

                    <div className='signWith'>
                        <span>â€” Or Sign In With â€”</span>
                    </div>

                    <div className="socialButtons">
                        <ClassButton
                            label={'Facebook'}
                            cssName={'buttonSocial'}
                        />
                        <ClassButton
                            label={'Twitter'}
                            cssName={'buttonSocial'}
                        />
                    </div>
                </div>
            </>
        )
    }
}
