const utilities = {
    checkPersonal: (input) => {
        if (input !== undefined) {
            return /^[\p{L}\p{N}*-]+$/u.test(input)
        };
    },

    checkEmailValidity: (email) => {
        if (email !== undefined) {
            return /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email);
        };
    },

    checkPasswordValidity: (password) => {
        if (password !== undefined) {
            return /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[.,!@#$%^&*])(?=.{8,})/.test(password);
        };
    },

    checkConfirmPasswordValidity: (password, confirmPassword) => {
        if (password !== undefined && confirmPassword !== undefined) {
            return password === confirmPassword;
        }
    },

    checkGender: (gender) => {
        if (gender !== undefined && gender !== '') {
            return true;
        }
    },

    checkDate: (date) => {
        // let today = (new Date()).toISOString().slice(0, 10)
        // console.log("data di oggi", today) // 2021/09/13

        let yearsAgo = new Date().setFullYear(new Date().getFullYear() -16)/1000

        if (new Date(date).getTime() / 1000 !== undefined && new Date(date).getTime() / 1000 > yearsAgo) {
            return true;
        }
    },

    generateCode: () => {
        return Math.floor(Math.random() * (99999 - 10000) + 10000);
    },

    checkCode: (code, codeInput) => {
        return code === codeInput;
    }
};

export default utilities;
