// import { faExclamationCircle } from "@fortawesome/free-solid-svg-icons";
import { Component } from "react"
import Title from '../component/funcComponents/Title';
import ClassInputBox from '../component/classComponents/ClassInputBox/ClassInputBox';
import ClassButton from '../component/classComponents/ClassButton/ClassButton';
import utilities from '../utilities/utilities';
import { Link } from 'react-router-dom';
import Select from "../component/funcComponents/Select";
import './Registration.css';
// TODO // 
//✅ Nome // Cognome 

// Genere // Data di nascita

// ✅Email

// ✅ Password
// ✅ Conferma Password

// Button Conferma

export default class Registration extends Component {
    constructor(props) {
        super(props)
        this.state = {
            customer: {
                name: '',
                surname: '',
                date: '',
                gender: '',
                email: '',
                password: '',
                confirmPassword: '',
            }
        }

    }

    handleName = (e) => { this.name = e }
    handleSurname = (e) => { this.surname = e }
    handleEmail = (e) => { this.email = e }
    handlePassword = (e) => { this.password = e }
    handleConfirmPassword = (e) => { this.confirmPassword = e }
    handleDate = (e) => { this.date = e }
    handleGender = (e) => { this.gender = e }

    handleSubmit = (e) => {
        if (
            utilities.checkPersonal(this.name) &&
            utilities.checkPersonal(this.surname) &&
            utilities.checkEmailValidity(this.email) &&
            utilities.checkPasswordValidity(this.password) &&
            utilities.checkConfirmPasswordValidity(this.password, this.confirmPassword) &&
            utilities.checkGender(this.gender) &&
            utilities.checkDate(this.date)) {

            console.log("nome: ", this.name)
            console.log("surname: ", this.surname)
            console.log("date: ", this.date)
            console.log("gender: ", this.gender)
            console.log("email: ", this.email)
            console.log("password: ", this.password)
            console.log("confirmPassword: ", this.confirmPassword)

            console.log("checkNome: ", utilities.checkPersonal(this.name))
            console.log("checkCognome: ", utilities.checkPersonal(this.surname))
            console.log("checkEmail: ", utilities.checkEmailValidity(this.email))
            console.log("checkPassword: ", utilities.checkPasswordValidity(this.password))
            console.log("checkConfirm: ", utilities.checkConfirmPasswordValidity(this.password, this.confirmPassword))
            console.log("checkDate: ", utilities.checkDate(this.date))
            console.log("checkGender: ", utilities.checkGender(this.gender))
            console.log("registrazione avvenuta con successo")

        } else {
            console.log("nome: ", this.name)
            console.log("surname: ", this.surname)
            console.log("date: ", this.date)
            console.log("gender: ", this.gender)

            console.log("email: ", this.email)
            console.log("password: ", this.password)
            console.log("confirmPassword: ", this.confirmPassword)

            console.log("checkNome: ", utilities.checkPersonal(this.name))
            console.log("checkCognome: ", utilities.checkPersonal(this.surname))
            console.log("checkEmail: ", utilities.checkEmailValidity(this.email))
            console.log("checkPassword: ", utilities.checkPasswordValidity(this.password))
            console.log("checkConfirm: ", utilities.checkConfirmPasswordValidity(this.password, this.confirmPassword))
            console.log("checkDate: ", utilities.checkDate(this.date))
            console.log("checkGender: ", utilities.checkGender(this.gender))

            console.log("registrazione fallita")

        }

        // this.setState({
        //     customer: {
        //         name: this.name,
        //         surname: this.surname,
        //         date: this.date,
        //         gender: this.gender,
        //         email: this.email,
        //         password: this.password,
        //         confirmPassword: this.confirmPassword
        //     }
        // })
    }

    render() {
        return (
            <>
                <Title title={"Registration"} subtitle={"Don't have an account?"} />
                <div className="mainForm">
                    <div className="Container-form">

                        <div className="personal">
                            <ClassInputBox
                                placeholder={'Name'}
                                name={'Name'}
                                type={'text'}
                                callbackInput={this.handleName}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Surname'}
                                name={'Surname'}
                                type={'text'}
                                callbackInput={this.handleSurname}
                                maxlength={24}
                                cssName={'text'}
                            />
                        </div>

                        <div className="data-gender">
                            <ClassInputBox
                                name={'dob'}
                                type={'date'}
                                callbackInput={this.handleDate}
                                maxlength={24}
                                cssName={'text'} />
                            <Select callbackSelectGender={this.handleGender} />
                        </div>

                        <div className="personal">
                            <ClassInputBox
                                placeholder={'Email'}
                                name={'Email'}
                                type={'email'}
                                callbackInput={this.handleEmail}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Password'}
                                name={'Password'}
                                type={'password'}
                                callbackInput={this.handlePassword}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Conferma password'}
                                name={'Conferma password'}
                                type={'password'}
                                callbackInput={this.handleConfirmPassword}
                                maxlength={24}
                                cssName={'text'}
                            />
                        </div>
                        <ClassButton
                            label={'SIGN UP'}
                            cssName={'Sign-in'}
                            callbackButton={this.handleSubmit}
                        />
                    </div>

                    <div className='signWith'>
                    <span>— Have an account? — </span>
                    <Link to="/login" style={{ color: 'white', textDecoration: 'none' }}>Sign in</Link>

                    </div>

                </div>
            </>
        )
    }
}
