import { Component } from 'react';
/* Import Style */
import './Login.css';
/* Iport Function Components */
import Title from '../component/funcComponents/Title';
/* Iport Class Components */
import ClassInputBox from '../component/classComponents/ClassInputBox';
import ClassButton from '../component/classComponents/ClassButton';
//icon
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye } from '@fortawesome/free-solid-svg-icons';
import { faEyeSlash } from '@fortawesome/free-solid-svg-icons';

class Login extends Component {

  constructor(props) {
    super(props)
    this.element = <FontAwesomeIcon icon={faEye} />
    this.elementDropper = <FontAwesomeIcon icon={faEyeSlash} />
    this.state = {
      errorUser: false,
      type: 'password',
      iconVision: this.element,
    }
  }

  // Controllo email con regex
  handleRegEx = (myre) => {
    var regex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return regex.test(myre)
  }

  // Salva User
  handleUsername = (e) => {
    this.elemU = e
  }

  // Salva Password
  handlePassword = (e) => {
    this.elemP = e
  }

  // Check Button 
  handleSign = () => {
    let warning = null;
    if ((this.elemU === undefined) || this.handleRegEx(this.elemU) === false) {
      warning = true
    } else if (this.elemP === undefined || this.elemP <= 4) {
      warning = true
    } else {
      warning = false
    }
    this.setState({
      errorUser: warning
    })
  }

  // Mostra Password
  handleShowpsw = () => {
    let types = ''
    let icon = null

    if (this.state.type === 'password') {
      types = 'text'
      icon = this.element
    } else {
      types = 'password'
      icon = this.elementDropper
    }

    this.setState({
      type: types,
      iconVision: icon
    })
  }

  render() {
    return (

      <div className='Container-font'>

        <header className='Login'>
          <Title
            cssTitle={'h1'}
            label={'Login#10'}
          />
          <Title
            cssTitle={'h3'}
            label={'Have an account?'}
          />
        </header>

        <form action='index.html' className="Container-form">

          <div>
            {
              this.state.errorUser === true &&
              <Title
                label={'Email o password errate'}
                cssTitle={'Error'}
              />
            }
          </div>

          <ClassInputBox
            placeholder={'Username'}
            name={'Username'}
            type={'email'}
            callbackInput={this.handleUsername}
            maxlength={24}
            cssName={'text'}
          />

          <div className='Container-password'>
            <div>
              <ClassInputBox
                placeholder={'Password'}
                name={'Password'}
                type={this.state.type}
                callbackInput={this.handlePassword}
                maxlength={24}
                cssName={'text'}
              />
            </div>
            <ClassButton
              label={this.state.iconVision}
              cssName={'Vision'}
              callbackButton={this.handleShowpsw}
            />
          </div>

          <ClassInputBox
            value={'SIGN IN'}
            cssName={'Sign-in'}
            callbackButton={this.handleSign}
            type={'submit'}
          />
        </form>

        <div className='Check' >
          <span>
            <input type='checkbox' id='RememberMe' />
            <label for='RememberMe'>Remember Me</label>
          </span>

          <ClassButton
            label={'Forgot Password'}
            cssName={'forgot-password'}
          />
        </div>

        <div style={{ color: 'white', textAlign: 'center', marginTop: '0.75rem', marginBottom: '0.75rem' }}>
          — Or Sign In With —
        </div>

        <footer className='footer'>
          <ClassButton
            label={'Facebook'}
            cssName={'Facebook'}
          />
          <ClassButton
            label={'Twitter'}
            cssName={'Twitter'}
          />
        </footer>
      </div>
    );
  }
}

export default Login;
