import { Component } from 'react';
import Title from '../component/funcComponents/Title';
import ClassInputBox from '../component/classComponents/ClassInputBox/ClassInputBox';
import ClassButton from '../component/classComponents/ClassButton/ClassButton';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye } from '@fortawesome/free-solid-svg-icons';
import { faEyeSlash } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

class Login extends Component {

  constructor(props) {
    super(props)
    this.element = <FontAwesomeIcon icon={faEye} />
    this.elementDropper = <FontAwesomeIcon icon={faEyeSlash} />
    this.state = {
      errorUser: false,
      type: 'password',
      iconVision: this.element,
    }
  }

  handleRegEx = (email) => { return /^(?=.{1,64}@)[A-Za-z0-9_-]+(\\.[A-Za-z0-9_-]+)*@[^-][A-Za-z0-9-]+(\\.[A-Za-z0-9-]+)*(\\.[A-Za-z]{2,})$"/.test(email) }
  handleUsername = (e) => { this.elemU = e }
  handlePassword = (e) => { this.elemP = e }

  handleShowpsw = () => {
    let types = ''
    let icon = null
    if (this.state.type === 'password') {
      types = 'text'
      icon = this.element
    } else {
      types = 'password'
      icon = this.elementDropper
    }
    this.setState({
      type: types,
      iconVision: icon
    })
  }

  handleSign = (e) => {
    let warning = false;
    if (this.handleRegEx(this.elemU) || this.elemP.length < 5) {
      warning = true
      e.preventDefault()
    }else{
      this.props.history.push("/login")
    }
    console.log("regex: ", this.handleRegEx(this.elemU))
    console.log("username: ", this.elemU)
    console.log("password: ", this.elemP)
    this.setState({
      errorUser: warning
    })


  }


  render() {
    return (
      <>
        <Title title={"Login"} subtitle={"Have an account?"} />

        <div className="mainForm">
          <form className="Container-form" onSubmit={this.handleSign}>

            <div>
              {
                this.state.errorUser === true &&
                <Title
                  subtitle={'Email o password errate'}
                />
              }
            </div>

            <ClassInputBox
              placeholder={'Username'}
              name={'Username'}
              type={'email'}
              callbackInput={this.handleUsername}
              maxlength={24}
              cssName={'text'}
            />

            <div className='Container-password'>
              <div>
                <ClassInputBox
                  placeholder={'Password'}
                  name={'Password'}
                  type={this.state.type}
                  callbackInput={this.handlePassword}
                  maxlength={24}
                  cssName={'text'}
                />
              </div>
              <ClassButton
                label={this.state.iconVision}
                cssName={'Vision'}
                callbackButton={this.handleShowpsw}
              />
            </div>

            <ClassInputBox
              value={'SIGN IN'}
              cssName={'Sign-in'}
              type={'submit'}
            />
          </form>

          <div className='Check' >
            <span>
              <input type='checkbox' id='RememberMe' />
              <label htmlFor='RememberMe'>Remember Me</label>
            </span>

            <ClassButton
              label={'Forgot Password'}
              cssName={'forgot-password'}
            />
            {/* </div> */}
          </div>


          <div className='signWith'>
            <Link to="/registration" style={{ color: 'white', textDecoration: 'none' }}>Registration</Link>
          </div>

          <div className='signWith'>
            <span>— Or Sign In With —</span>
          </div>

          <div className="socialButtons">
            <ClassButton
              label={'Facebook'}
              cssName={'buttonSocial'}
            />
            <ClassButton
              label={'Twitter'}
              cssName={'buttonSocial'}
            />
          </div>
        </div>

      </>
    );
  }
}

export default Login;
