import { Component } from 'react';
import './Login.css';
import Title from '../component/funcComponents/Title';
import ClassInputBox from '../component/classComponents/ClassInputBox';
import ClassButton from '../component/classComponents/ClassButton';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye } from '@fortawesome/free-solid-svg-icons';
import { faEyeSlash } from '@fortawesome/free-solid-svg-icons';

class Login extends Component {

  constructor(props) {
    super(props)
    this.element = <FontAwesomeIcon icon={faEye} />
    this.elementDropper = <FontAwesomeIcon icon={faEyeSlash} />
    this.state = {
      errorUser: false,
      type: 'password',
      iconVision: this.element,
    }
  }

  // Controllo email con regex
  handleRegEx = (myre) => {
    var regex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return regex.test(myre)
  }

  // Salva User
  handleUsername = (e) => { this.elemU = e }

  // Salva Password
  handlePassword = (e) => { this.elemP = e }

  // Check Button 
  handleSign = () => {
    let warning = null;
    if ((this.elemU === undefined) || this.handleRegEx(this.elemU) === false) {
      warning = true
    } else if (this.elemP === undefined || this.elemP <= 4) {
      warning = true
    } else {
      warning = false
    }
    this.setState({
      errorUser: warning
    })
  }

  // Mostra Password
  handleShowpsw = () => {
    let types = ''
    let icon = null

    if (this.state.type === 'password') {
      types = 'text'
      icon = this.element
    } else {
      types = 'password'
      icon = this.elementDropper
    }

    this.setState({
      type: types,
      iconVision: icon
    })
  }

  render() {
    return (
      <section className="sectionLogin">
        <div className='Container-font'>
          <header className='Login'>
            <div>
              <h1>Login #10</h1>
            </div>
          </header>
          <div>
            <h3>Have an account?</h3>
          </div>

          <div className="mainForm">
            <form action='index.html' className="Container-form">

              <div>
                {
                  this.state.errorUser === true &&
                  <Title
                    label={'Email o password errate'}
                    cssTitle={'Error'}
                  />
                }
              </div>

              <ClassInputBox
                placeholder={'Username'}
                name={'Username'}
                type={'email'}
                callbackInput={this.handleUsername}
                maxlength={24}
                cssName={'text'}
              />

              <div className='Container-password'>
                <div>
                  <ClassInputBox
                    placeholder={'Password'}
                    name={'Password'}
                    type={this.state.type}
                    callbackInput={this.handlePassword}
                    maxlength={24}
                    cssName={'text'}
                  />
                </div>
                <ClassButton
                  label={this.state.iconVision}
                  cssName={'Vision'}
                  callbackButton={this.handleShowpsw}
                />
              </div>

              <ClassInputBox
                value={'SIGN IN'}
                cssName={'Sign-in'}
                callbackInput={this.handleSign}
                type={'submit'}
              />
            </form>

            <div className='Check' >
              <span>
                <input type='checkbox' id='RememberMe' />
                <label htmlFor='RememberMe'>Remember Me</label>
              </span>

              <ClassButton
                label={'Forgot Password'}
                cssName={'forgot-password'}
              />
              {/* </div> */}
            </div>
            <div className='signWith'>
              <span>— Or Sign In With —</span>
            </div>

            <div className="socialButtons">
              <ClassButton
                label={'Facebook'}
                cssName={'buttonSocial'}
              />
              <ClassButton
                label={'Twitter'}
                cssName={'buttonSocial'}
              />
            </div>
          </div>



        </div>
      </section>
    );
  }
}

export default Login;
