// import { faExclamationCircle } from "@fortawesome/free-solid-svg-icons";
import { Component } from "react"
import Title from '../component/funcComponents/Title';
import ClassInputBox from '../component/classComponents/ClassInputBox/ClassInputBox';
import ClassButton from '../component/classComponents/ClassButton/ClassButton';
import utilities from '../utilities/utilities';
import { Link } from "react-router-dom";
import Select from "../component/funcComponents/Select";
// TODO // 
//✅ Nome // Cognome 

// Genere // Data di nascita

// Email

// Password
// Conferma Password

// Button Conferma

export default class Registration extends Component {
    constructor(props) {
        super(props)
        this.state = {
            customer: {
                name: '',
                surname: '',
                gender: '',
                email: '',
                password: '',
                confirmPassword: '',
            }
        }
    }

    handleName = (e) => {
        this.name = e
    }

    handleSurname = (e) => {
        this.surname = e
    }

    handleEmail = (e) => {
        this.email = e
    }

    handlePassword = (e) => {
        this.password = e
    }

    handleConfirmPassword = (e) => {
        this.confirmPassword = e
    }

    handleSelect = (e) => {
        this.gender = e
    }

    handleSubmit() {
        if (utilities.checkEmailValidity(this.email) &&
            utilities.checkPasswordValidity(this.name) &&
            utilities.checkPersonal(this.name) &&
            utilities.checkPersonal(this.surname)) {
            console.log("registrazione avvenuta con successo")
        }

        this.setState({
            customer: {
                name: this.name,
                surname: this.surname,

                email: this.email,
                password: this.password,

            }
        })
    }

    render() {
        return (
            <>
                <Title title={"Registration"} subtitle={"Don't have an account?"} />
                <div className="mainForm">
                    <div className="Container-form">

                        <div className="personal">
                            <ClassInputBox
                                placeholder={'Name'}
                                name={'Name'}
                                type={'text'}
                                callbackInput={this.handleName}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Surname'}
                                name={'Surname'}
                                type={'text'}
                                callbackInput={this.handleSurname}
                                maxlength={24}
                                cssName={'text'}
                            />
                        </div>

                        <div className="data-gender">
                            {/* data di nascita */}
                            <Select callbackSelectGender={this.handleSelect} />
                        </div>

                        <div className="personal">
                            <ClassInputBox
                                placeholder={'Email'}
                                name={'Email'}
                                type={'text'}
                                callbackInput={this.handleEmail}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Password'}
                                name={'Password'}
                                type={'password'}
                                callbackInput={this.handlePassword}
                                maxlength={24}
                                cssName={'text'}
                            />
                            <ClassInputBox
                                placeholder={'Conferma password'}
                                name={'Conferma password'}
                                type={'password'}
                                callbackInput={this.handleConfirmPassword}
                                maxlength={24}
                                cssName={'text'}
                            />
                        </div>
                        <ClassButton
                            label={'SIGN UP'}
                            cssName={'Sign-in'}
                            callbackButton={this.handleSubmit}
                        />
                    </div>

                    <div className='Check' >
                        <span>
                            <input type='checkbox' id='RememberMe' />
                            <label htmlFor='RememberMe'>Remember Me</label>
                        </span>

                        <ClassButton
                            label={'Forgot Password'}
                            cssName={'forgot-password'}
                        />
                        {/* </div> */}
                    </div>

                    <div className='signWith'>
                        <Link to="/registration" style={{ color: 'white', textDecoration: 'none' }}>Sign up</Link>
                    </div>

                    <div className='signWith'>
                        <span>— Or Sign In With —</span>
                    </div>

                    <div className="socialButtons">
                        <ClassButton
                            label={'Facebook'}
                            cssName={'buttonSocial'}
                        />
                        <ClassButton
                            label={'Twitter'}
                            cssName={'buttonSocial'}
                        />
                    </div>
                </div>
            </>
        )
    }
}
